sys_atf_schedule.condition:
function testforWeekend(){
	var run = test; 
	var rightnow = new GlideDateTime();
	var day = rightNow.getDayOfweekUTC();

	if( day == 6 || day == 7) {
		gs.info("Demo scheduled was not run because today is weekend day");
		run = false;
	}
	return run; 
}
testForWeekend();
